<script context="module" lang="ts">
	import { t, waitLocale, register, init } from 'svelte-intl-precompile';
  import { goto } from '$app/navigation';
  import { page } from '$app/stores';
  register('en', () => import('$locales/en.js'));
  register('es', () => import('$locales/es.js'));
  let defaultLang = 'en';
  let localeRegex = /^\/((es|en)(-\w\w)?)/;
  export async function load({ page: { path } }) {
    const lang = (localeRegex.exec(path) || [null, defaultLang])[1];
    init({
      initialLocale: lang,
      fallbackLocale: 'en'
    });	    
    await waitLocale()
    return {};
  }
</script>
<script lang="ts">
  function setLocale(code) {
    if ($page.path === '/') {
      goto('/' + code, { noscroll: true });
    } else {
      let newPath = $page.path.replace(localeRegex, '/' + code);
      goto(newPath, { noscroll: true })
    }
  }
</script>

